mapper.select:
select pda.id, pda.name, road.name, inspector.name, pda.`status`, pda.create_time
from pda,
road,
inspector

    pda.road_id = road.id
    and inspector.pda_id = pda.id


    and pda.name = #{pdaName}


    and inspector.name = #{inspectorName}


    and road.name = #{roadName}

limit #{skipRow},#{pageSize}

select count(0)
from pda,
road,
inspector

    pda.road_id = road.id
    and inspector.pda_id = pda.id


    and pda.name = #{pdaName}


    and inspector.name = #{inspectorName}


    and road.name = #{roadName}
